const initialState = {
    tasks: [],
    loading: false,
    error: null,
  };
  
  export const taskReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'FETCH_TASKS_SUCCESS':
        return {
          ...state,
          tasks: action.payload,
        };
      case 'FETCH_TASKS_FAILURE':
        return {
          ...state,
          error: action.payload,
        };
      // Handle other actions (CREATE, UPDATE, DELETE) here
      default:
        return state;
    }
  };
