// src/redux/actions/taskActions.js
import axios from 'axios';
import { FETCH_TASKS, FETCH_TASK_BY_ID, CREATE_TASK, UPDATE_TASK } from './types';

// Fetch all tasks
export const fetchTasks = () => async (dispatch) => {
  try {
    const res = await axios.get('/api/tasks');
    dispatch({ type: FETCH_TASKS, payload: res.data });
  } catch (err) {
    console.error(err);
  }
};

// Fetch task by ID
export const fetchTaskById = (id) => async (dispatch) => {
  try {
    const res = await axios.get(`/api/tasks/${id}`);
    dispatch({ type: FETCH_TASK_BY_ID, payload: res.data });
  } catch (err) {
    console.error(err);
  }
};

// Create new task
export const createTask = (taskData) => async (dispatch) => {
  try {
    const res = await axios.post('/api/tasks', taskData);
    dispatch({ type: CREATE_TASK, payload: res.data });
  } catch (err) {
    console.error(err);
  }
};

// Update task
export const updateTask = (id, taskData) => async (dispatch) => {
  try {
    const res = await axios.put(`/api/tasks/${id}`, taskData);
    dispatch({ type: UPDATE_TASK, payload: res.data });
  } catch (err) {
    console.error(err);
  }
};
