import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createTask, updateTask } from '../redux/actions/taskActions';
// src/components/TaskForm.js

const TaskForm = ({ match, history }) => {
  const dispatch = useDispatch();
  const taskId = match.params.id;
  const [task, setTask] = useState({
    title: '',
    description: '',
    status: 'Pending',
    dueDate: '',
  });

  useEffect(() => {
    if (taskId) {
      // Fetch task by ID and set form fields
      dispatch(fetchTaskById(taskId));
    }
  }, [dispatch, taskId]);

  const handleChange = (e) => {
    setTask({ ...task, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (taskId) {
      dispatch(updateTask(taskId, task));
    } else {
      dispatch(createTask(task));
    }
    history.push('/');
  };

  return (
    <form onSubmit={handleSubmit} className="task-form">
      <div className="form-group">
        <label htmlFor="title">Title</label>
        <input type="text" name="title" value={task.title} onChange={handleChange} required />
      </div>
      <div className="form-group">
        <label htmlFor="description">Description</label>
        <textarea name="description" value={task.description} onChange={handleChange} required />
      </div>
      <div className="form-group">
        <label htmlFor="status">Status</label>
        <select name="status" value={task.status} onChange={handleChange} required>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
        </select>
      </div>
      <div className="form-group">
        <label htmlFor="dueDate">Due Date</label>
        <input type="date" name="dueDate" value={task.dueDate} onChange={handleChange} required />
      </div>
      <div className="form-group">
        <input type="submit" value={taskId ? 'Update Task' : 'Create Task'} />
      </div>
    </form>
  );
};

export default TaskForm;
