import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { createTask } from '../redux/actions/taskActions';

const TaskForm = () => {
    const dispatch = useDispatch();
    const [task, setTask] = useState({ title: '', description: '', status: '', dueDate: '' });

    const handleChange = (e) => {
        setTask({ ...task, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(createTask(task));
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>Create Task</h1>
            <label>
                Title:
                <input type="text" name="title" value={task.title} onChange={handleChange} required />
            </label>
            <label>
                Description:
                <textarea name="description" value={task.description} onChange={handleChange} required />
            </label>
            <label>
                Status:
                <input type="text" name="status" value={task.status} onChange={handleChange} required />
            </label>
            <label>
                Due Date:
                <input type="date" name="dueDate" value={task.dueDate} onChange={handleChange} required />
            </label>
            <button type="submit">Create Task</button>
        </form>
    );
};

export default TaskForm;
