import { fetchTasks as fetchTasksService, createTask as createTaskService } from '../../api/taskService';

// Action Types
export const FETCH_TASKS_SUCCESS = 'FETCH_TASKS_SUCCESS';
export const CREATE_TASK_SUCCESS = 'CREATE_TASK_SUCCESS';

// Action Creators
export const fetchTasks = () => async (dispatch) => {
    try {
        const tasks = await fetchTasksService();
        dispatch({ type: FETCH_TASKS_SUCCESS, payload: tasks });
    } catch (error) {
        console.error('Failed to fetch tasks:', error);
    }
};

export const createTask = (task) => async (dispatch) => {
    try {
        const newTask = await createTaskService(task);
        dispatch({ type: CREATE_TASK_SUCCESS, payload: newTask });
    } catch (error) {
        console.error('Failed to create task:', error);
    }
};
